import logo from './logo.svg';
import './App.css';
import "./GlossaryButtonStyles.css";
import "./ConceptDisplayStyles.css";
import "./AddConceptStyles.css";

import {useEffect,useState} from 'react';
import Sequencing from "./Sequencing.png";
import Selection from "./Selection.png";
import Iteration from "./Iteration.png";

import ConceptDisplay from "./ConceptDisplay";
import AddConcept from './AddConcept';
import GlossaryButton from "./GlossaryButton";


function App() {



  const [concepts, setConcepts]=useState([{
    title: "IT programming /software",
    description: <p>The first<u>control structure</u>that 
                  dictates the logical flow of the program.
                  Lines of code are executed chronologically, 
                  from top to bottom.</p>,
    imgSource : Sequencing,
    complete :false
  },
  {
    title: "Design",
    description: <p>The second<u>control structure</u>. Selectively executing a block of 
                  code depending on when a condition is true or not.</p>,
    imgSource : Selection,
    complete :false
  },
  {
    title: "Business",
    description: <p>The third<u>control structure</u>
                .Continuously executing a block of code if a condtion is true.</p>,
    imgSource : Iteration,
    complete :false
  },
  {
    title: "Marketing",
    description: <p>A<u>prodefined algorithm</u>
                .That can be invoked (told to run) at any.</p>,
    imgSource : Iteration
  },  
  {
    title: "Finance",
    description: <p>The container in code.It hold a data type. Variables have idenfilers,
                type Variables allow for <u>peristant date</u>
                within the program.</p>,
    imgSource : Iteration,
    complete :false
  }
])
  const [selectedConcept, setSelectedConcept] = useState(concepts[0])
  const [numCompleted, setNumCompleted] = useState(0)
  const [addingPrinciple, setAddingPrinciple] = useState(false)

  function nextItem(){

    if (numCompleted < concepts.length){
      setNumCompleted(numCompleted+1)
    }
    let index = concepts.indexOf(selectedConcept)
    let temp= [...concepts]
    temp[index].complete = true
    console.log(temp)
    setConcepts(temp)

    concepts[index].complete=true

    if (index+1<concepts.length){
      setSelectedConcept(concepts[index+1])
      console.log(index)
    }else{
      setSelectedConcept(concepts[0])
      console.log(index)
    }
  }

  const buttons = concepts.map((concept, index) => {
    return (
      <GlossaryButton label = {concept.title} 
      action ={()=> {setSelectedConcept(concept)}}
      complete = {concept.complete}
      />
    )
  })

  return (
    <div className="App">
      <header className="App-header">
      <div className='glossary-left'>

<table border="1"> 
<tr> 
<th className='glossary-top' colspan="3">Top Menu:Career development </th> 
</tr> 

<tr> 
<td className='glossary-right'>{buttons}</td> 
    <td className='glossary-left'>
          { addingPrinciple == true && 
            <AddConcept/>
          }
          {
            addingPrinciple == false && 
            <ConceptDisplay selectedConcept={selectedConcept}
            concept = {concepts} nextItem={nextItem}
            numCompleted= {numCompleted}/>
          }
    </td>
</tr> 
<tr> 
<th className='glossary-bottom'colspan="3">Bottom template</th> 
</tr> 

</table> 


</div>

      </header>
    </div>
  );
}

export default App;
